"use strict";
// Basic Data Types:
// 1. Primitive Types:
// String
let firstName = "Akhtar";
// Number
let age = 25;
// Boolean
let isActive = true;
// Undefined
let x = undefined;
// Null
let y = null;
// Inferred
let z;
// Any
let hobby = ["A", "B", "C", "D", "E"];
hobby = "Gardening";
hobby = undefined;
// 2. Non primitive types
// Array
let students = ["Rachel", "Monica", "Chandler"];
let roll = [2, 4, 5];
// Tuple
let coordinates = [1, 5];
let teacher = [34, "Mark", true];
